class DBHelper{static get DATABASE_URL(){return"http://localhost:1337/restaurants"}static getDB(){this.restaurantDBPromise=idb.open("restaurants-db",1,function(upgradeDB){switch(upgradeDB.oldVersion){case 0:upgradeDB.createObjectStore("restaurants",{keyPath:"id"})}})}static status(response){return response.status>=200&&response.status<300?Promise.resolve(response):Promise.reject(new Error(`Request failed. Returned status of ${response.statusText}`))}static json(response){return response.json()}static fetchRestaurants(callback){fetch(DBHelper.DATABASE_URL).then(DBHelper.status).then(DBHelper.json).then(function(restaurants){DBHelper.restaurantDBPromise.then(function(db){if(db){var store=db.transaction("restaurants","readwrite").objectStore("restaurants");restaurants.forEach(function(restaurant){store.put(restaurant)})}}),callback(null,restaurants)}).catch(function(error){DBHelper.restaurantDBPromise.then(function(db){if(db)return db.transaction("restaurants").objectStore("restaurants").getAll().then(function(restaurants){callback(null,restaurants)})}).catch(function(){callback(error,null)})})}static fetchRestaurantById(id,callback){DBHelper.fetchRestaurants((error,restaurants)=>{if(error)callback(error,null);else{const restaurant=restaurants.find(r=>r.id==id);restaurant?callback(null,restaurant):callback("Restaurant does not exist",null)}})}static fetchRestaurantByCuisine(cuisine,callback){DBHelper.fetchRestaurants((error,restaurants)=>{if(error)callback(error,null);else{const results=restaurants.filter(r=>r.cuisine_type==cuisine);callback(null,results)}})}static fetchRestaurantByNeighborhood(neighborhood,callback){DBHelper.fetchRestaurants((error,restaurants)=>{if(error)callback(error,null);else{const results=restaurants.filter(r=>r.neighborhood==neighborhood);callback(null,results)}})}static fetchRestaurantByCuisineAndNeighborhood(cuisine,neighborhood,callback){DBHelper.fetchRestaurants((error,restaurants)=>{if(error)callback(error,null);else{let results=restaurants;"all"!=cuisine&&(results=results.filter(r=>r.cuisine_type==cuisine)),"all"!=neighborhood&&(results=results.filter(r=>r.neighborhood==neighborhood)),callback(null,results)}})}static fetchNeighborhoods(callback){DBHelper.fetchRestaurants((error,restaurants)=>{if(error)callback(error,null);else{const neighborhoods=restaurants.map((v,i)=>restaurants[i].neighborhood),uniqueNeighborhoods=neighborhoods.filter((v,i)=>neighborhoods.indexOf(v)==i);callback(null,uniqueNeighborhoods)}})}static fetchCuisines(callback){DBHelper.fetchRestaurants((error,restaurants)=>{if(error)callback(error,null);else{const cuisines=restaurants.map((v,i)=>restaurants[i].cuisine_type),uniqueCuisines=cuisines.filter((v,i)=>cuisines.indexOf(v)==i);callback(null,uniqueCuisines)}})}static urlForRestaurant(restaurant){return`./restaurant.html?id=${restaurant.id}`}static imageUrlForRestaurant(restaurant){return`/img/${restaurant.photograph?restaurant.photograph+".webp":"No_image.svg"}`}static imageSrcSetAttrForRestaurant(restaurant){const fileName=restaurant.photograph;return fileName?`/img/${fileName}.webp 1x, /img/${fileName}@2x.webp 2x`:""}static mapMarkerForRestaurant(restaurant,map){return new google.maps.Marker({position:restaurant.latlng,title:restaurant.name,url:DBHelper.urlForRestaurant(restaurant),map,animation:google.maps.Animation.DROP})}}